import React, { useState, useEffect } from "react";

function Actor() {
    const [inputVal, setInputVal] = useState("");
    const [showData, setshowData] = useState([]);

    let dataUrl = "";
    if (inputVal.length > 0) {
        dataUrl = `https://api.tvmaze.com/search/shows?q=${inputVal}`;
    } else {
        dataUrl = `https://api.tvmaze.com/search/shows?q=friends`;
    }

    const getshowData = async () => {
        try {
            let respone = await fetch(dataUrl);
            let resData = await respone.json();
            setshowData(resData);
        } catch (error) {
            console.log(error);
        }
    };
    useEffect(() => {
        getshowData();
    }, [inputVal]);
    // console.log(showData);
    return (
        <>
            <section className="mt-5">
                <div className="container">
                    <div className="row">
                        <div className="col-md-7">
                            <input
                                type="text"
                                value={inputVal}
                                onChange={(e) => setInputVal(e.target.value)}
                                className="form-control"
                                placeholder="Search by Show name...."
                            />
                        </div>
                    </div>
                </div>
            </section>

            <section>
                <div className="container mt-4">
                    <div className="row">
                        {showData.map((element) => {
                            return (
                              <div className="col-md-4 mb-3">
                                <div className="card">
                                  {element.show.image ? (
                                    <img
                                      src={element.show.image.medium}
                                      class="poster"
                                      alt={
                                        element.show.name != null
                                          ? element.show.name
                                          : "Not found"
                                      }
                                    />
                                  ) : (
                                    <div
                                      className="poster"
                                      style={{ height: "325px" }}
                                    >
                                      <img
                                        src="https://www.prokerala.com/movies/assets/img/no-poster-available.jpg"
                                        style={{
                                          width: "230px",
                                          height: "325px",
                                        }}
                                      />
                                    </div>
                                  )}
                                  <div className="card-body">
                                    <p
                                      className="card-text overflow-hidden"
                                      style={{ height: "90px" }}
                                    >
                                      {element.show.summary}
                                    </p>
                                    <span>
                                      <i
                                        class="fa fa-star text-success"
                                        aria-hidden="true"
                                      ></i>{" "}
                                      {element.show.rating.average}
                                    </span>
                                  </div>

                                  <h5 className="text-danger text-center">
                                    {element.show.name}
                                  </h5>
                                </div>
                              </div>
                            );
                        })}
                    </div>
                </div>
            </section>
        </>
    );
}

export default Actor;
